  text-align: right;
}

#genePanelsPagination {
  font-size: .85em;
  float: right;
}

#genePanelsPaginationWrapper {
  display: block;
  margin-top: 1em;
}

#genePanelsDownloadWrapper {
  float: left;
}

#genePanelsExcludeGenesWrapper {
  float: right;
  font-size: .7em;
  text-transform: capitalize;
  margin-right: -20px;
  color: #4D5860;
}

#genePanelsExcludeGenesWrapper label {
  width: 100%;
  display: inline;
  padding-left: .3em;
  font-weight: bold;
}

#genePanelsPagination > .fa:hover {
  background-color: rgba(0,0,0,0.1);
  color: $theme.linkColor;
  cursor: pointer;
  transition: all 0.2s linear;
}
